Shapes.Script:

 collection.MIN_COL_WIDTH = 5;
 collection.getMaxColumns = function (rawText) {
     var a = F.parseTextArray(rawText);

     var c = 0;
     for (var i in a) {
         c = Math.max(c, a[i].length);
     }

     return c;
 };
 collection.getMaxXForColumnHandle = function (i, properties) {
     var cols = this.getMaxColumns(properties["content"].value);
     if (i < cols - 2) {
         return properties["h" + (i + 1)].x - this.MIN_COL_WIDTH;
     }

     return properties["box"].w - this.MIN_COL_WIDTH;
 };
 collection.getMinXForColumnHandle = function (i, properties) {
     if (i > 0) {
         return properties["h" + (i - 1)].x + this.MIN_COL_WIDTH;
     }

     return this.MIN_COL_WIDTH;
 };
 collection.isColumnHandleDisabled = function (i, properties) {
     var cols = this.getMaxColumns(properties["content"].value);
     return i >= cols - 1;
 };

 collection.getCalendarDate = function (dateStr, startDayNum) {
    var date = new Date(dateStr);
    if (date == "Invalid Date") date = new Date();
    var month = date.getMonth();
    var year = date.getFullYear();
    var lastDayOfMonth = new Date(year, month + 1, 0).getDate();
    var firstDayOfMonth = new Date(year, month, 1).getDay();
    var calendarArray = [];
    if (startDayNum == 2) {
     if (firstDayOfMonth != 6) firstDayOfMonth += 7;
    }
    if (startDayNum == 0) firstDayOfMonth = (firstDayOfMonth == 0 ? 7 : firstDayOfMonth);
    var dayCount = 1;
    var firstIndex = 0;
    if (startDayNum == 0) firstIndex = 1;
    if (startDayNum == 2) firstIndex = 6;

    for (var i = firstIndex; i < 42 + firstIndex; i++) {
        var result = {};
        var space = i - firstDayOfMonth;
        space++;
        var dayValue = new Date(year, month, space).getDate();
        result["value"] = dayValue;
        if (i < firstDayOfMonth || space > lastDayOfMonth) result["disabled"] = true;
        calendarArray[dayCount] = result;
        dayCount++;
    }
    return calendarArray;
};

