For.DomContent:
var month = $monthContent.toString();
var start = parseInt($start.value);
var pickDay = parseInt($pickDay.value);
var calendarArray = collection.getCalendarDate(month,start);
var gElement = F._target;
var bBox = [];
for (var i = 1; i < 43; i++) {
    var date = calendarArray[i];
    Dom.empty(gElement);
    var nodeTemp = Dom.newDOMElement({
        _name: "text",
        _uri: "http://www.w3.org/2000/svg",
        _text : date.value
    })
    gElement.appendChild(nodeTemp);
    bBox.push(gElement.getBBox());
}
Dom.empty(gElement);
var specs =[];
var a = $strokeStyle.w;
var boxW = ($box.w - (20 + 2*a)) / 7;
var boxH = (($box.h - 8) - a) / 8;
var dateCount = 1;
for (var i = 0; i < 6; i++) {
    for (var ii = 0 ; ii < 7; ii++) {
        var x = 10 + a + (boxW * ii);
        var y = boxH * (i + 2);
        var date = calendarArray[dateCount];
        var css = new CSS();
        css.set("font-size",$textFont.size);
        css.set("font-family",$textFont.family);
        css.set("font-style",$textFont.style);
        css.set("font-weight",$textFont.weight);
        css.set("font-decor",$textFont.decor);
        css.set("storke","none");
        var fillColor = Color.fromString("#000");
        if (start == 0) {
            if (ii == 6) {
                fillColor = Color.fromString("#ff3333");
            } else if (ii == 5) {
                fillColor = Color.fromString("#0099ff");
            }
        } else if (start == 1) {
            if (ii == 0) {
                fillColor = Color.fromString("#ff3333");
            } else if (ii == 6) {
                fillColor = Color.fromString("#0099ff");
            }
        } else {
            if (ii == 1) {
                fillColor = Color.fromString("#ff3333");
            } else if (ii == 0) {
                fillColor = Color.fromString("#0099ff");
            }
        }
        if (date.disabled) {
            css.set("fill", fillColor.hollowed(0.5).toRGBAString());
        } else {
            css.set("fill", fillColor.hollowed(0).toRGBAString());
        }

        var node = {
            _name: "text",
            _uri: "http://www.w3.org/2000/svg",
            _text : date.value,
            x: x,
            y: y,
            dx: (boxW - bBox[(dateCount - 1)].width) / 2,
            dy: (((boxH - 6) - bBox[(dateCount - 1)].height) / 2) + bBox[(dateCount - 1)].height,
            style: css
        }
        specs.push(node);

        var dsw = $dayStrokeStyle.w ;
        if (!date.disabled) {
            x = 13 + a + (boxW * ii);
            y = (boxH * (i + 2)) + 3;
            var css = new CSS();
            css.set("stroke-width", $dayStrokeStyle.w);
