// giao diem cua elipse lon va duong thang (handle b, tam elipse)
var a = $box.w/2;
var b = $box.h/2;
var y = 0;
var x = 0;
var i = 1/Math.sqrt(1/(a*a) + (($b.y - b)*($b.y - b))/(b*b*($b.x-a)*($b.x-a)) );
var j = i*(($b.y - b)/($b.x -a))
if($b.x >= $box.w/2)
{
    x = a + i;
    y = b + j;
}
else
{
    x = a - i ;
    y = b - j;
}
// het tinh giao diem

